" use strict ";
// циклы используються для повторения одних и тех же действий 
// т.к мы меняем переменную ниже то вместо const пишем let 
let num = 50;
// мы говорим коду пока наше условие не выполненно -выполняй такие то действия 
// как в реальной жизни - пока заряжен ноутбук мы работаем
while (num <= 55) {
    // пока у нас num меньше 55 мы помещаяем действие
    console.log(num);
    // если запустить этот код то он конечно будет работать бесконечно т.к 50 меньше 55 и мы тогда пишем
    num++;
    // добавляем по 1 при помощи инкремента, поменяем переменную

}

